import React, { useState, useEffect } from "react";
import API from '../api/api';
import RestaurantModal from './RestaurantModal'
import UserService from "../services/user.service";

const Home = () => {
  const [modalShow, setModalShow] = useState(false);
  const [button, setButton] = useState(null);
  const [data, setDataValue] = useState(null);  

  let openModal = (state, button) =>{
    setModalShow(state)
    setButton(button)
  }

  useEffect(() => {
    async function fetchData() {
      const asyncResponse = await API.get("restaurants")
      setDataValue(asyncResponse.data)
    }
    fetchData();
  },[]);

  return (
    <section>
      <div className="body-main">
        <div className="body-main-container">
        <div><button className="body-container-button" onClick={() => openModal(true, 0)}>Bouton</button></div>
        <div><button className="body-container-button" onClick={() => openModal(true, 1)}>Bouton</button></div>
        </div>
      </div>
      <RestaurantModal
        show={modalShow}
        button={button}
        data={data}
        onHide={() => setModalShow(false)}
      />
    </section>
  )
}

export default Home;
