import React, {Component, useState, useEffect} from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import API from './api/api';


const MyVerticallyCenteredModal = (props) => {
  const [data, setDataValue] = useState(null);
  const cancelTokenSource = CancelToken.source();

  useEffect(() => {
    async function fetchData() {
      try {
        const asyncResponse = await API.get("restaurants", {
          cancelToken: cancelTokenSource.token,
        });

        const res = asyncResponse.data

        if (componentIsMounted.current) {
          setDataValue(res);
          console.log(data[props.button])
        }
      } catch (err) {
        console.error(err);
      }
    }
    fetchData();
  },[]);
  return (
    <Modal dialogClassName="modal-container"
      {...props}
      centered
    >
      <Modal.Body>
        <button onClick={props.onHide} type="button" className="close">
					×
				</button>
        <div className="modal-restaurant-img">
          <img src={"image.jpeg"}/>
        </div>
        <div className="modal-restaurant-name-category">
          <h4>Dumbo</h4>
          <p>Burgers</p>
        </div>
        <div className="modal-restaurant-adress-town">
          <p>64 rue Jean-Baptiste Pigalle, 75009</p>
          <p>Paris.</p>
        </div>
        <div className="modal-restaurant-details">
          <div className="modal-restaurant-details-icons">
            <div className="modal-restaurant-details-icons-wrapper">
              <img src={"004-fast-time.svg"}/>
            </div>
            <p>Sur le pouce</p>
          </div>
          <div className="modal-restaurant-details-icons">
            <div className="modal-restaurant-details-icons-wrapper">
              <img src={"005-hamburger.svg"}/>
            </div>
            <p>Comfort food</p>
          </div>
          <div className="modal-restaurant-details-icons">
            <div className="modal-restaurant-details-icons-wrapper">
              <img src={"003-meat.svg"}/>
            </div>
            <p>Carnivore</p>
          </div>
          <div className="modal-restaurant-details-icons">
            <div className="modal-restaurant-details-icons-wrapper">
              <img src={"006-piggybank.svg"}/>
            </div>
            <p>Moins de 15€</p>
          </div>
        </div>
      </Modal.Body>

    </Modal>
  );
}


const App = () => {
  const [modalShow, setModalShow] = useState(false);
  const [button, setButton] = useState(null);

  let openModal = (state, button) =>{
    setModalShow(state)
    setButton(button)
  }

  return (
    <>
    <section>
      <div className="body-main">
        <div className="body-main-container">
        <div><button className="body-container-button" onClick={() => openModal(true, 1)}>Boutton</button></div>
        <div><button className="body-container-button" onClick={() => openModal(true, 2)}>Boutton</button></div>
        </div>
      </div>
    </section>
      <MyVerticallyCenteredModal
      show={modalShow}
      button={button}
      onHide={() => setModalShow(false)}
    />
  </>
  )
}


export default App;
