import React, { useState, useEffect } from "react";
import RestaurantModal from './RestaurantModal'
import DataService from "../services/data.service"

const Home = () => {
    const [modalShow, setModalShow] = useState(false);
    const [button, setButton] = useState(null);
    const [data, setData] = useState(null)
    const [restaurantModal, setRestaurantModal] = useState(null)
    let openModal = (value) =>{
        setModalShow(true)
        setButton(value)
    }

    useEffect(() => {
        let restaurantModal;
        DataService.getAllRestaurant().then(
            (response) => {
                setData(response.data)
                setRestaurantModal = (
                    <RestaurantModal
                        show={modalShow}
                        button={button}
                        onHide={() => setModalShow(false)}
                        data={data}
                    />
                )
                console.log(data)
        })



    },[]);


  return (
    <section>
      <div className="body-main">
        <div className="body-main-container">
        <div><button className="body-container-button" onClick={() => openModal(0)}>Bouton</button></div>
        <div><button className="body-container-button" onClick={() => openModal(1)}>Bouton</button></div>
        </div>
      </div>
      {restaurantModal}

    </section>
  )
}

export default Home;
