import React, {Component} from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


const MyVerticallyCenteredModal = (props) => {
  return (
    <Modal
      {...props}
      centered
      size="sm"
    >
      <Modal.Body className="modal-body">
        <button onClick={props.onHide} type="button" className="close">
					√ó
				</button>
        <img src={"bouillon47.jpg"}/>
        <h4 id="modal-restaurant-name">Title</h4>
        <p id="modal-restaurant-category">Category</p>
        <p id="modal-restaurant-adress">Adress</p>
        <p id="modal-restaurant-town">Town</p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}


const App = () => {
  const [modalShow, setModalShow] = React.useState(false);
  return (
    <>
    <section>
      <div className="body-main">
        <div className="body-main-container">
        <div><button className="body-container-button" onClick={() => setModalShow(true)}>Boutton</button></div>
        <div><button className="body-container-button" onClick={() => setModalShow(true)}>Boutton</button></div>
        </div>
      </div>
    </section>
    <MyVerticallyCenteredModal
    show={modalShow}
    onHide={() => setModalShow(false)}
  />
  </>
  )
}


export default App;
