import React, {useState, useEffect} from 'react'
import Modal from 'react-bootstrap/Modal';
import API from './api/api';


const MyVerticallyCenteredModal = (props) => {  
  let data = props.button == null ? null : props.data[props.button]
  if (data){
    return (
      <Modal dialogClassName="modal-container"
      {...props}
      centered
    >
      <Modal.Body>
        <button onClick={props.onHide} type="button" className="close">
					×
				</button>
        <div className="modal-restaurant-img">
          <img src={data.mainPicture} alt={data.name}/>
        </div>
        <div className="modal-restaurant-name-category">
          <h4>{data.name}</h4>
          <p>{data.type}</p>
        </div>
        <div className="modal-restaurant-adress-town">
          <p>{data.adress}</p>
          <p>{data.town}.</p>
        </div>
        <div className="modal-restaurant-details">
            {
              data.details.split(',').map(function(item){
              return (
                        <div className="modal-restaurant-details-icons" key={item}>
                          <div className="modal-restaurant-details-icons-wrapper" id={item === "France" ? "country" : null } >
                            <img src={`${item.split(' ').join('_').toLowerCase()}.svg`} alt={item}/>
                          </div>
                          <p>{item}</p>
                        </div>          
                      )

              })
            }
          <div className="modal-restaurant-details-icons">
            <div className="modal-restaurant-details-icons-wrapper">
              <img src={data.price <= 15 ? "smallprice.svg" : "midprice.svg"} alt={data.price}/>
            </div>
            {data.price <= 15 ? <p>Moins de 15€</p> : <p>Entre 30€ et 50€</p>}
          </div>
        </div>
      </Modal.Body>

    </Modal>
  );
  }else{
    return(null)
  }
}


const App = () => {
  const [modalShow, setModalShow] = useState(false);
  const [button, setButton] = useState(null);
  const [data, setDataValue] = useState(null);  

  let openModal = (state, button) =>{
    setModalShow(state)
    setButton(button)
  }

  useEffect(() => {
    async function fetchData() {
      const asyncResponse = await API.get("restaurants")
          setDataValue(asyncResponse.data)
    }
    fetchData();
  },[]);

  return (
    <>
    <section>
      <div className="body-main">
        <div className="body-main-container">
        <div><button className="body-container-button" onClick={() => openModal(true, 0)}>Boutton</button></div>
        <div><button className="body-container-button" onClick={() => openModal(true, 1)}>Boutton</button></div>
        </div>
      </div>
    </section>
      <MyVerticallyCenteredModal
      show={modalShow}
      button={button}
      data={data}
      onHide={() => setModalShow(false)}
    />
  </>
  )
}


export default App;
